import React, { useEffect, useState } from 'react';

export default function ShowSong(props) {
    const [songs, setSongs] = useState([]);
    const [audioUrl, setAudioUrl] = useState('');
    const [audioElement, setAudioElement] = useState(null);

    useEffect(() => {
        const fetchSongs = async () => {
            try {
                const response = await fetch(`http://localhost:5000/fetchSong/${props.email}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch songs');
                }
                const data = await response.json();
                setSongs(data.songs);
            } catch (error) {
                console.error('Error fetching songs:', error);
            }
        };

        fetchSongs();
    }, [props.email]);

    const playSong = async (query) => {
        try {
            setAudioUrl(`http://localhost:5000/fetchurl/${query}`);
        } catch (error) {
            console.error('Error playing song:', error);
        }
    };

    const handleAudioRef = (audio) => {
        if (audio) {
            setAudioElement(audio);
        }
    };

    const handlePlayPause = () => {
        if (audioElement.paused) {
            audioElement.play();
        } else {
            audioElement.pause();
        }
    };

    const handleBackward = () => {
        audioElement.currentTime -= 10; // Go back 10 seconds
    };

    const handleForward = () => {
        audioElement.currentTime += 10; // Go forward 10 seconds
    };

    return (
        <div>
            <h1>ShowSong</h1>
            <ul>
                {songs.map((song, index) => (
                    <li key={index}>
                        <p>{song}</p>
                        <button onClick={() => playSong(song)}>Play</button>
                    </li>
                ))}
            </ul>
            {audioUrl && (
                <div>
                    <audio ref={handleAudioRef} controls>
                        <source src={audioUrl} type="audio/mpeg" />
                        Your browser does not support the audio element.
                    </audio>
                    <button onClick={handlePlayPause}>Play/Pause</button>
                    <button onClick={handleBackward}>Backward</button>
                    <button onClick={handleForward}>Forward</button>
                </div>
            )}
        </div>
    );
}
