import React, { useEffect, useState } from 'react';

export default function ShowSong( prop) {
    const [songs, setSongs] = useState([]);

    useEffect(() => {
        const fetchSongs = async () => {
            try {
                const response = await fetch(`http://localhost:5000/fetchSong/${props.email}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch songs');
                }
                const data = await response.json();
                setSongs(data.songs);
            } catch (error) {
                console.error('Error fetching songs:', error);
            }
        };

        fetchSongs();
    }, [props.email]);

    const playSong = (songUrl) => {
        // Logic to play the song goes here
        console.log('Playing song:', songUrl);
    };

    return (
        <div>
            <h1>ShowSong</h1>
            <ul>
                {songs.map((song, index) => (
                    <li key={index}>
                        <span>{song.name}</span>
                        <button onClick={() => playSong(song.url)}>Play</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}
