import React, { useEffect, useState } from 'react';


export default function Song() {
    const [songs, setSongs] = useState([]);
    const [audioUrl, setAudioUrl] = useState('');
    const [currImage, setCurrImage] = useState('');
    const [queryParam, setQueryParam] = useState('');
    const [imageUrls, setImageUrls] = useState({});
    const [popupVisible, setPopupVisible] = useState(false);

    useEffect(() => {
        const getQueryParam = async () => {
            const urlParams = await new URLSearchParams(window.location.search);
            setQueryParam(urlParams.get('playlistId'));
        };
        getQueryParam();
    }, []);

    useEffect(() => {
        const fetchSongs = async () => {
            try {
                const response = await fetch(`http://localhost:5000/fetchSong/${queryParam}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch songs');
                }
                const data = await response.json();
                setSongs(data.songs);
            } catch (error) {
                console.error('Error fetching songs:', error);
            }
        };
        fetchSongs();
    }, [queryParam]);

    useEffect(() => {
        const fetchImages = async () => {
            const urls = {};
            await Promise.all(
                songs.map(async (song) => {
                    try {
                        const response = await fetch(`http://localhost:5000/fetchImage/${song}`);
                        if (!response.ok) {
                            throw new Error('Failed to fetch image');
                        }
                        const data = await response.json();
                        urls[song] = data.url;
                    } catch (error) {
                        console.error('Error fetching image:', error);
                    }
                })
            );
            setImageUrls(urls);
        };

        fetchImages();
    }, [songs]);

    const playSong = async (query) => {
        try {
            const response = await fetch(`http://localhost:5000/fetchUrl/${query}`);
            if (!response.ok) {
                throw new Error('Failed to fetch song URL');
            }
            const data = await response.json();
            setAudioUrl(data.downloadUrl);
            setCurrImage(data.imageUrl);
            setPopupVisible(true);
        } catch (error) {
            console.error('Error playing song:', error);
        }
    };

    const closePopup = () => {
        setPopupVisible(false);
    };

    return (
        <div className='player'>
            <div className='song-list'>
                <h1>ShowSong</h1>
                <ul>
                    {songs.map((song, index) => (
                        <li key={index}>
                            <button className="button" onClick={() => playSong(song)}>

                            <button>
    P L A Y
    <div id="clip">
        <div id="leftTop" class="corner"></div>
        <div id="rightBottom" class="corner"></div>
        <div id="rightTop" class="corner"></div>
        <div id="leftBottom" class="corner"></div>
    </div>
    <span id="rightArrow" class="arrow"></span>
    <span id="leftArrow" class="arrow"></span>
</button>


                            </button>
                        </li>
                    ))}
                </ul>
            </div>

            {/* Button to open popup */}
            <button className={`open-popup-btn ${popupVisible ? 'hidden' : ''}`} onClick={() => setPopupVisible(true)}>Open Popup</button>

            {/* Popup */}
            <div className={`popup ${popupVisible ? 'active' : ''}`}>
                <button className="close-btn" onClick={closePopup}>Close</button>
                <img src={currImage} alt="" />
                <audio src={audioUrl} controls autoPlay></audio>
            </div>
        </div>
    );
}
