import React, { useState } from 'react'
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import Cookies from 'js-cookie';

export default function Login() {
	const [email, setemail] = useState('');
	const [password, setPassword] = useState('');
	const navigate = useNavigate();

	const handleSubmit = (event) => {
		event.preventDefault();

		fetch(`http://localhost:5000/login`, {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json'
			},
			body: JSON.stringify({ email, password })
		})
			.then(response => {
				if (response.ok) {
					return response.json(); // Parse the response body as JSON
				} else {
					throw new Error('Login failed');
				}
			})
			.then(data => {

				Cookies.set('sessionId', data.sessionId); // Set the sessionId cookie
				navigate('/'); // Navigate to the home page
			})
			.catch(error => {
				console.error('Error:', error); // Handle fetch error or login failure
			});

	};
	return (
		<div>
			<form onSubmit={handleSubmit}>

				<label htmlFor="user_email">Userid or Email</label>
				<input
					type="text"
					id="user_email"
					name="email"
					value={email}
					required
					onChange={(event) => setemail(event.target.value)}
				/>
				<br />
				<label htmlFor="password">Password</label>
				<input
					type="password" // Use type="password" for password field
					id="password"
					name="password"
					value={password}
					required
					onChange={(event) => setPassword(event.target.value)}
				/>
				<button type="submit">Login</button>
			</form>
			<p>
				Don't have an account? <Link to="/register">Register</Link>
			</p>




















		</div>
	)
}
