import React, { useEffect, useState } from 'react';

export default function ShowSong(props) {
    const [songs, setSongs] = useState([]);

    useEffect(() => {
        const fetchSongs = async () => {
            try {
                const response = await fetch(`http://localhost:5000/fetchSong/${props.email}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch songs');
                }
                const data = await response.json();
                setSongs(data.songs);
            } catch (error) {
                console.error('Error fetching songs:', error);
            }
        };

        fetchSongs();
    }, [props.email]);

    const playSong = async (query) => {
        try {
            const response = await fetch(`http://localhost:5000/fetchSong/${query}`);
            if (!response.ok) {
                throw new Error('Failed to fetch song details');
            }
            const data = await response.json();
            console.log('Playing song:', query);
            console.log('Song details:', data);
            // Logic to play the song goes here
        } catch (error) {
            console.error('Error fetching song details:', error);
        }
    };

    return (
        <div>
            <h1>ShowSong</h1>
            <ul>
                {songs.map((song, index) => (
                    <li key={index}>
                        <p>{song}</p>
                        <button onClick={() => playSong(song)}>Play</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}
