import React, { useEffect, useState } from 'react';

export default function ShowSong(props) {
    const [songs, setSongs] = useState([]);
    const [audioUrl, setAudioUrl] = useState('');

    useEffect(() => {
        const fetchSongs = async () => {
            try {
                const response = await fetch(`http://localhost:5000/fetchSong/${props.email}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch songs');
                }
                const data = await response.json();
                setSongs(data.songs);
            } catch (error) {
                console.error('Error fetching songs:', error);
            }
        };

        fetchSongs();
    }, [props.email]);

    const playSong = async (query) => {
        // try {
        //     const response = await fetch(`http://localhost:5000/fetchurl/${query}`);
        //     if (!response.ok) {
        //         throw new Error('Failed to fetch song details');
        //     }
        //     const blob = await response.blob();
        //     const url = URL.createObjectURL(blob);
        //     setAudioUrl(url);
        //     console.log(url);
        // } catch (error) {
        //     console.error('Error fetching song details:', error);
        // }
    };

    return (
        <div>
            <h1>ShowSong</h1>
            <ul>
                {songs.map((song, index) => (
                    <li key={index}>
                        <p>{song}</p>
                        <button onClick={() => playSong(song)}>Play</button>
                    </li>
                ))}
            </ul>
            {audioUrl && (
                <audio controls>
                    <source src={audioUrl} type="audio/mpeg" />
                    Your browser does not support the audio element.
                </audio>
            )}
        </div>
    );
}
