export default function GetPlaylist( props ) {

    const [code, setcode] = useState('');

    const handleauth = async (event) => {
        fetch('http://localhost:5000/auth').then(response => {
            if (response.ok) {
                return response.json(); // Parse the response body as JSON
            } else {
                throw new Error('Login failed');
            }
        })
            .then(data => {


                window.location.href = data.authorizeURL; // Navigate to the home page
            })
            .catch(error => {
                console.error('Error:', error); // Handle fetch error or login failure
            });

    }
    fetch(`http://localhost:5000/fetchImage/${song}`).then(response => {
        if (response.ok) {
            return response.json(); // Parse the response body as JSON
        } else {
            throw new Error('Login failed');
        }
    })
        .then(data => {


            return data.url;// Navigate to the home page
        })

    useEffect(() => {
        const handleAuthorization = () => {

            const urlParams = new URLSearchParams(window.location.search);
            setcode(urlParams.get('code'));

        }
        console.log(props.email);
        handleAuthorization();
    }, []);

    return (
        <div>
            <button onClick={handleauth}>authrizespotify</button>
            <form action={`http://localhost:5000/search`} method='POST'>
                <label htmlFor="url">enter the spotify url</label>
                <input
                    type="text"
                    name="url"
                    id='url'
                />
                <input type="hidden" name="code" value={code} />
                <input type="hidden" name="email" value={props.email} />
                <button type='submit'>Submit</button>
            </form>
        </div>
    )
}
