import React from 'react'
import { useLocation } from 'react-router-dom';

export default function ShowPlaylist() {
    const navigate = useNavigate();
    const location = useLocation();
    const handleauth = async (event) => {
        fetch('http://localhost:5000/auth').then(response => {
            if (response.ok) {
                return response.json(); // Parse the response body as JSON
            } else {
                throw new Error('Login failed');
            }
        })
        .then(data => {

             // Set the sessionId cookie
             window.location.href = data.authorizeURL; // Navigate to the home page
        })
        .catch(error => {
            console.error('Error:', error); // Handle fetch error or login failure
        });
    }

    useEffect(() => {
        const searchParams = new URLSearchParams(location.search);
        const query = searchParams.get('query');
        if (query) {
          setQueryParam(query);
        }
      }, [location]);

    return (
        <div>
            <button onClick={handleauth}>authrizespotify</button>
            <form action={`http://localhost:5000/search`} method='POST'>
                <label htmlFor="url">enter the spotify url</label>
                <input
                    type="text"
                    name="url"
                    id='url'
                />
                <button type='submit'>Submit</button>
            </form>
        </div>
    )
}
